INSERT INTO `rm1_options` VALUES("317", "_site_transient_wporg_theme_feature_list", "a:3:{s:6:\"Layout\";a:7:{i:0;s:11:\"grid-layout\";i:1;s:10:\"one-column\";i:2;s:11:\"two-columns\";i:3;s:13:\"three-columns\";i:4;s:12:\"four-columns\";i:5;s:12:\"left-sidebar\";i:6;s:13:\"right-sidebar\";}s:8:\"Features\";a:20:{i:0;s:19:\"accessibility-ready\";i:1;s:10:\"buddypress\";i:2;s:17:\"custom-background\";i:3;s:13:\"custom-colors\";i:4;s:13:\"custom-header\";i:5;s:11:\"custom-menu\";i:6;s:12:\"editor-style\";i:7;s:21:\"featured-image-header\";i:8;s:15:\"featured-images\";i:9;s:15:\"flexible-header\";i:10;s:14:\"footer-widgets\";i:11;s:20:\"front-page-post-form\";i:12;s:19:\"full-width-template\";i:13;s:12:\"microformats\";i:14;s:12:\"post-formats\";i:15;s:20:\"rtl-language-support\";i:16;s:11:\"sticky-post\";i:17;s:13:\"theme-options\";i:18;s:17:\"threaded-comments\";i:19;s:17:\"translation-ready\";}s:7:\"Subject\";a:9:{i:0;s:4:\"blog\";i:1;s:10:\"e-commerce\";i:2;s:9:\"education\";i:3;s:13:\"entertainment\";i:4;s:14:\"food-and-drink\";i:5;s:7:\"holiday\";i:6;s:4:\"news\";i:7;s:11:\"photography\";i:8;s:9:\"portfolio\";}}", "no");
INSERT INTO `rm1_options` VALUES("319", "theme_mods_hestia", "a:26:{i:0;b:0;s:18:\"custom_css_post_id\";i:-1;s:18:\"nav_menu_locations\";a:3:{s:7:\"primary\";i:2;s:6:\"footer\";i:3;s:12:\"top-bar-menu\";i:0;}s:13:\"show_on_front\";s:4:\"page\";s:16:\"hestia_team_hide\";b:1;s:24:\"hestia_testimonials_hide\";b:1;s:16:\"hestia_blog_hide\";b:1;s:27:\"hestia_big_title_background\";s:83:\"http://localhost:8888/reviewsmvp1/wp-content/uploads/2017/08/scientific-2040795.jpg\";s:22:\"hestia_big_title_title\";s:27:\"Grow your Research Team Now\";s:21:\"hestia_big_title_text\";s:72:\"Show prospective students and post-docs why they will thrive in your lab\";s:28:\"hestia_big_title_button_text\";s:7:\"Sign Up\";s:21:\"hestia_features_title\";s:60:\"Get applicants familiar with your lab before they even apply\";s:24:\"hestia_features_subtitle\";s:0:\"\";s:23:\"hestia_features_content\";s:1196:\"[{\"icon_value\":\"fa-address-card-o\",\"color\":\"#e91e63\",\"text\":\"Use our comprehensive profile builder to show applicants what they can expect from their time in your lab in terms of mentoring, publishing, technical experience, and more.\",\"link\":\"#\",\"text2\":\"undefined\",\"title\":\"Tell applicants who you are\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40b56\",\"shortcode\":\"undefined\"},{\"icon_value\":\"fa-users\",\"color\":\"#00bcd4\",\"text\":\"You know your team is awesome, and applicants should to. Our lab member profile will let your current researchers tell about their experiences working with you and with each other. \",\"link\":\"#\",\"text2\":\"undefined\",\"title\":\"Show off your team\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40b66\",\"shortcode\":\"undefined\"},{\"icon_value\":\"fa-home\",\"color\":\"#4caf50\",\"text\":\"Link your lab website directly to your profile and let applicants that visit know all the reasons your lab should become theirs.\",\"link\":\"#\",\"text2\":\"undefined\",\"title\":\"Do it all from Home\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40b86\",\"shortcode\":\"undefined\"}]\";s:27:\"hestia_testimonials_content\";s:1401:\"[{\"text\":\"&quot;We have no regrets! After using your product my business skyrocketed! I made back the purchase price in just 48 hours! I couldn&#039;t have asked for more than this.&quot;\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost:8888\\/reviewsmvp1\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/5.jpg\",\"title\":\"Inverness McKenzie\",\"subtitle\":\"Business Owner\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40d56\",\"shortcode\":\"undefined\"},{\"text\":\"&quot;Your company is truly upstanding and is behind its product 100 percent. Hestia is worth much more than I paid. I like Hestia more each day because it makes easier.&quot;\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost:8888\\/reviewsmvp1\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/6.jpg\",\"title\":\"Hanson Deck\",\"subtitle\":\"Independent Artist\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40d66\",\"shortcode\":\"undefined\"},{\"text\":\"&quot;Thank you for making it painless, pleasant and most of all hassle free! I am so pleased with this product. Dude, your stuff is great! I will refer everyone I know.&quot;\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost:8888\\/reviewsmvp1\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/7.jpg\",\"title\":\"Natalya Undergrowth\",\"subtitle\":\"Freelancer\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40d76\",\"shortcode\":\"undefined\"}]\";s:19:\"hestia_team_content\";s:4369:\"[{\"icon_value\":\"fa-facebook\",\"text\":\"Locavore pinterest chambray affogato art party, forage coloring book typewriter. Bitters cold selfies, retro celiac sartorial mustache.\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost:8888\\/reviewsmvp1\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/1.jpg\",\"title\":\"Desmond Purpleson\",\"subtitle\":\"CEO\",\"social_repeater\":\"[{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb908674e06&quot;,&quot;link&quot;:&quot;facebook.com&quot;,&quot;icon&quot;:&quot;fa-facebook&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9148530ft&quot;,&quot;link&quot;:&quot;plus.google.com&quot;,&quot;icon&quot;:&quot;fa-google-plus&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9148530fc&quot;,&quot;link&quot;:&quot;twitter.com&quot;,&quot;icon&quot;:&quot;fa-twitter&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9150e1e89&quot;,&quot;link&quot;:&quot;linkedin.com&quot;,&quot;icon&quot;:&quot;fa-linkedin&quot;}]\",\"id\":\"customizer_repeater_56d7ea7f40c56\",\"shortcode\":\"undefined\"},{\"icon_value\":\"fa-facebook\",\"text\":\"Craft beer salvia celiac mlkshk. Pinterest celiac tumblr, portland salvia skateboard cliche thundercats. Tattooed chia austin hell.\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost:8888\\/reviewsmvp1\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/2.jpg\",\"title\":\"Parsley Pepperspray\",\"subtitle\":\"Marketing Specialist\",\"social_repeater\":\"[{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9155a1072&quot;,&quot;link&quot;:&quot;facebook.com&quot;,&quot;icon&quot;:&quot;fa-facebook&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9160ab683&quot;,&quot;link&quot;:&quot;twitter.com&quot;,&quot;icon&quot;:&quot;fa-twitter&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9160ab484&quot;,&quot;link&quot;:&quot;pinterest.com&quot;,&quot;icon&quot;:&quot;fa-pinterest&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb916ddffc9&quot;,&quot;link&quot;:&quot;linkedin.com&quot;,&quot;icon&quot;:&quot;fa-linkedin&quot;}]\",\"id\":\"customizer_repeater_56d7ea7f40c66\",\"shortcode\":\"undefined\"},{\"icon_value\":\"fa-facebook\",\"text\":\"Pok pok direct trade godard street art, poutine fam typewriter food truck narwhal kombucha wolf cardigan butcher whatever pickled you.\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost:8888\\/reviewsmvp1\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/3.jpg\",\"title\":\"Desmond Eagle\",\"subtitle\":\"Graphic Designer\",\"social_repeater\":\"[{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb917e4c69e&quot;,&quot;link&quot;:&quot;facebook.com&quot;,&quot;icon&quot;:&quot;fa-facebook&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb91830825c&quot;,&quot;link&quot;:&quot;twitter.com&quot;,&quot;icon&quot;:&quot;fa-twitter&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb918d65f2e&quot;,&quot;link&quot;:&quot;linkedin.com&quot;,&quot;icon&quot;:&quot;fa-linkedin&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb918d65f2x&quot;,&quot;link&quot;:&quot;dribbble.com&quot;,&quot;icon&quot;:&quot;fa-dribbble&quot;}]\",\"id\":\"customizer_repeater_56d7ea7f40c76\",\"shortcode\":\"undefined\"},{\"icon_value\":\"fa-github-square\",\"text\":\"Small batch vexillologist 90&#039;s blue bottle stumptown bespoke. Pok pok tilde fixie chartreuse, VHS gluten-free selfies wolf hot.\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost:8888\\/reviewsmvp1\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/4.jpg\",\"title\":\"Ruby Von Rails\",\"subtitle\":\"Lead Developer\",\"social_repeater\":\"[{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb925cedcg5&quot;,&quot;link&quot;:&quot;github.com&quot;,&quot;icon&quot;:&quot;fa-github-square&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb925cedcb2&quot;,&quot;link&quot;:&quot;facebook.com&quot;,&quot;icon&quot;:&quot;fa-facebook&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb92615f030&quot;,&quot;link&quot;:&quot;twitter.com&quot;,&quot;icon&quot;:&quot;fa-twitter&quot;},{&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb9266c223a&quot;,&quot;link&quot;:&quot;linkedin.com&quot;,&quot;icon&quot;:&quot;fa-linkedin&quot;}]\",\"id\":\"customizer_repeater_56d7ea7f40c86\",\"shortcode\":\"undefined\"}]\";s:17:\"hestia_about_hide\";b:1;s:23:\"hestia_contact_subtitle\";s:53:\"Tell us how you use the site and what can be improved\";s:26:\"hestia_contact_content_new\";s:0:\"\";s:25:\"hestia_contact_background\";s:82:\"http://localhost:8888/reviewsmvp1/wp-content/uploads/2017/08/gecko-800887_1920.jpg\";s:16:\"background_image\";s:0:\"\";s:18:\"hestia_page_editor\";s:826:\"<h2 class=\"title\" style=\"text-align: center\" title=\"Shift-click to edit this element.\">About Us</h2><h5 class=\"description\" style=\"text-align: center\" title=\"Shift-click to edit this element.\">We allow you to show prospective researchers why your lab is a place where they can grow professionally and personally. With your profile you can tell students about your mentoring style and professional expectations. To further show how awesome your lab is we let current lab members make accounts and leave stories about their experiences working with you.  To show students your profile just link your lab website to your account. When a quality researcher visits your homepage, don\'t let them go before they even get a chance to know you.</h5><p> </p><p>[shorten and lead with independent clauses first sentence is a runon]</p>\";s:24:\"hestia_feature_thumbnail\";s:86:\"http://localhost:8888/reviewsmvp1/wp-content/uploads/2017/08/mechanics-424130_1920.jpg\";s:28:\"hestia_big_title_button_link\";s:43:\"http://localhost:8888/reviewsmvp1/register/\";s:11:\"custom_logo\";s:0:\"\";s:19:\"hestia_top_bar_hide\";b:1;}", "yes");
