INSERT INTO `rm1_posts` VALUES("95", "1", "2017-08-24 19:48:38", "2017-08-24 23:48:38", "{\n    \"hestia::hestia_big_title_text\": {\n        \"value\": \"Show students why they will thrive in your lab\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1\n    }\n}", "", "", "trash", "closed", "closed", "", "70f5ae3a-b303-408c-8005-760dcb1eac55", "", "", "2017-08-24 19:48:38", "2017-08-24 23:48:38", "", "0", "http://localhost:8888/reviewsmvp1/2017/08/24/70f5ae3a-b303-408c-8005-760dcb1eac55/", "0", "customize_changeset", "", "0");
INSERT INTO `rm1_posts` VALUES("96", "1", "2017-08-24 20:38:02", "2017-08-25 00:38:02", "{\n    \"hestia::hestia_features_title\": {\n        \"value\": \"How We Help\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1\n    },\n    \"hestia::hestia_features_subtitle\": {\n        \"value\": \"We allow you to show prospective researchers why your lab is a place where they can grow professionally and personally. Link your homepage to your account so that you don\'t lose a quality researcher before they even get a chance to know you.\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1\n    },\n    \"hestia::hestia_big_title_text\": {\n        \"value\": \"Show prospective students and post-docs why they will thrive in your lab\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1\n    },\n    \"hestia::hestia_features_content\": {\n        \"value\": \"[{\\\"icon_value\\\":\\\"fa-hand-spock-o\\\",\\\"color\\\":\\\"#e91e63\\\",\\\"text\\\":\\\"Tell students how you can help them develop professionally by telling about your mentoring style and work expectations before they ever submit an application.\\\",\\\"link\\\":\\\"#\\\",\\\"text2\\\":\\\"undefined\\\",\\\"title\\\":\\\"Introduce Yourself\\\",\\\"subtitle\\\":\\\"undefined\\\",\\\"social_repeater\\\":\\\"undefined\\\",\\\"id\\\":\\\"customizer_repeater_56d7ea7f40b56\\\",\\\"shortcode\\\":\\\"undefined\\\"},{\\\"icon_value\\\":\\\"fa-users\\\",\\\"color\\\":\\\"#00bcd4\\\",\\\"text\\\":\\\"Show students how awesome your team is by letting current lab members leave stories about their experiences.\\\",\\\"link\\\":\\\"#\\\",\\\"text2\\\":\\\"undefined\\\",\\\"title\\\":\\\"Introduce your Team\\\",\\\"subtitle\\\":\\\"undefined\\\",\\\"social_repeater\\\":\\\"undefined\\\",\\\"id\\\":\\\"customizer_repeater_56d7ea7f40b66\\\",\\\"shortcode\\\":\\\"undefined\\\"},{\\\"icon_value\\\":\\\"fa-file-text-o\\\",\\\"color\\\":\\\"#4caf50\\\",\\\"text\\\":\\\"Excite students with details about your curr