INSERT INTO `rm1_posts` VALUES("118", "1", "2017-08-26 21:14:01", "2017-08-27 01:14:01", "{\n    \"hestia::hestia_about_hide\": {\n        \"value\": false,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1\n    },\n    \"hestia::hestia_page_editor\": {\n        \"value\": \"<h2 class=\\\"title\\\" style=\\\"text-align: center;\\\" title=\\\"Shift-click to edit this element.\\\">How We Help</h2><h5 class=\\\"description\\\" style=\\\"text-align: center;\\\" title=\\\"Shift-click to edit this element.\\\">We allow you to show prospective researchers why your lab is a place where they can grow professionally and personally. With your [Name] profile you can tell students about your mentoring style and professional expectations. Link your lab website to your account so that, when a quality researcher visits your homepage, you don\'t lose them before they even get a chance to know you.</h5>\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1\n    },\n    \"hestia::hestia_feature_thumbnail\": {\n        \"value\": \"http://localhost:8888/reviewsmvp1/wp-content/uploads/2017/08/mechanics-424130_1920.jpg\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1\n    }\n}", "", "", "trash", "closed", "closed", "", "8b339361-9d64-4ece-b30d-3a063879427f", "", "", "2017-08-26 21:14:01", "2017-08-27 01:14:01", "", "0", "http://localhost:8888/reviewsmvp1/?p=118", "0", "customize_changeset", "", "0");
INSERT INTO `rm1_posts` VALUES("119", "1", "2017-08-26 21:19:27", "2017-08-27 01:19:27", "{\n    \"hestia::hestia_page_editor\": {\n        \"value\": \"<h2 class=\\\"title\\\" style=\\\"text-align: center;\\\" title=\\\"Shift-click to edit this element.\\\">About Us</h2><h5 class=\\\"description\\\" style=\\\"text-align: center;\\\" title=\\\"Shift-click to edit this element.\\\">We allow you to show prospective researchers why your lab is a place where they can grow professionally and personally. With your profile you can tell students about your mentoring style and professional expectations. To further show how awesome your lab is we let current lab members make accounts and leave stories about their experiences working with you.\\u00a0 To show students your profile just link your lab website to your account. When a quality researcher visits your homepage, don\'t let them go before they even get a chance to know you.</h5><p>\\u00a0</p><p>[shorten and lead with independent clauses first sentence is a runon]</p>\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1\n    }\n}", "", "", "trash", "closed", "closed", "", "5fc84bb5-547e-4e7a-b79f-5668adce0aab", "", "", "2017-08-26 21:19:27", "2017-08-27 01:19:27", "", "0", "http://localhost:8888/reviewsmvp1/?p=119", "0", "customize_changeset", "", "0");
