INSERT INTO `rm1_posts` VALUES("123", "1", "2017-08-28 20:06:48", "2017-08-29 00:06:48", "{\n    \"hestia::hestia_features_content\": {\n        \"value\": \"[{\\\"icon_value\\\":\\\"fa-address-card-o\\\",\\\"color\\\":\\\"#e91e63\\\",\\\"text\\\":\\\"Let applicants know why they should join your lab with our comprehensive PI profile builder. Your profile allows you to show applicants what they can expect in terms of mentoring, publishing, technical experience, and more.\\\",\\\"link\\\":\\\"#\\\",\\\"text2\\\":\\\"undefined\\\",\\\"title\\\":\\\"Tell applicants who you are\\\",\\\"subtitle\\\":\\\"undefined\\\",\\\"social_repeater\\\":\\\"undefined\\\",\\\"id\\\":\\\"customizer_repeater_56d7ea7f40b56\\\",\\\"shortcode\\\":\\\"undefined\\\"},{\\\"icon_value\\\":\\\"fa-users\\\",\\\"color\\\":\\\"#00bcd4\\\",\\\"text\\\":\\\"You know your team is awesome, and applicants should to. Our lab member profile will let your current researchers tell about their experiences working with you and with each other. \\\",\\\"link\\\":\\\"#\\\",\\\"text2\\\":\\\"undefined\\\",\\\"title\\\":\\\"Show off your awesome team\\\",\\\"subtitle\\\":\\\"undefined\\\",\\\"social_repeater\\\":\\\"undefined\\\",\\\"id\\\":\\\"customizer_repeater_56d7ea7f40b66\\\",\\\"shortcode\\\":\\\"undefined\\\"},{\\\"icon_value\\\":\\\"fa-home\\\",\\\"color\\\":\\\"#4caf50\\\",\\\"text\\\":\\\"Reach students from your home page by adding our &quot;learn more&quot; button to your lab&#39;s website. When applicants come to learn about your research, don&#39;t let them leave without getting to know you, your team, and all the reasons they should join your lab.\\\",\\\"link\\\":\\\"#\\\",\\\"text2\\\":\\\"undefined\\\",\\\"title\\\":\\\"Do it all from Home\\\",\\\"subtitle\\\":\\\"undefined\\\",\\\"social_repeater\\\":\\\"undefined\\\",\\\"id\\\":\\\"customizer_repeater_56d7ea7f40b86\\\",\\\"shortcode\\\":\\\"undefined\\\"}]\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1\n    },\n    \"hestia::he